window.consentReady = function(data) {
	const {
		DTEvent,
		DTStorage,
	} = window;

	DTStorage.set('consent_data', data);

	DTEvent.promise('dt:consent:ready', false, 0);
}

window.dataLayer = window.dataLayer || [];
(function(dtdata, dataLayer) {
	dataLayer.push(dtdata.getAll());
	dtdata.addCallback(function(key, val) {
		if (key) {
			if (typeof key == 'object') {
				dataLayer.push(key);
			} else {
				var obj = {};
				obj[key] = val;
				dataLayer.push(obj);
			}
		}
	});
}(DTData, dataLayer));

