header.script:
(function() {
	function isVisible(el) {
		return !!(el.offsetWidth || el.offsetHeight || el.getClientRects().length);
	}

	var navEl = document.querySelector('.b-nav');

	var menuToggleEl = navEl.querySelector('.b-nav__toggle--menu');
	var menuTriggerEl = navEl.querySelector('.b-nav__trigger--menu');
	var searchInputEl = navEl.querySelector('.b-nav__drawer--search input');
	var searchToggleEl = navEl.querySelector('.b-nav__toggle--search');
	var searchTriggerEl = navEl.querySelector('.b-nav__trigger--search');

	searchTriggerEl.addEventListener('click', function() {
		menuToggleEl.checked = false;

		window.requestAnimationFrame(function() {
			if (isVisible(searchInputEl)) {
				searchInputEl.focus();
			} else {
				searchInputEl.blur();
			}
		});
	});

	menuTriggerEl.addEventListener('click', function() {
		searchToggleEl.checked = false;
		searchInputEl.blur();
	});

	window.addEventListener('click', function(e) {
		if (!navEl.contains(e.target)) {
			menuToggleEl.checked = false;

			searchToggleEl.checked = false;
			searchInputEl.blur();
		}
	});
})();

