					function watchConnatix() {
						const watch = (player) => {
							const platform = 'connatix';
							const strategy = {
								platform: platform,
								searchTags: ['DIV'],
								verify: function (elem) {
									return (' ' + elem.className + ' ').indexOf(' b-connatix ') !== -1;
								},
								subscribe: function (elem) {
									player.on('videoStarted', function (playedVideoMetadata) {
										PARSELY.video.onPlay(player, playedVideoMetadata.id, {
											duration: playedVideoMetadata.duration,
											image_url: playedVideoMetadata.thumbnail,
											pub_date_tmsp: playedVideoMetadata.publishDate,
											title: playedVideoMetadata.title,
											author: playedVideoMetadata.contentStudio,
											tags: playedVideoMetadata.keywords,
											video_platform: platform
										});
									});
									player.on('pause', function (pausedVideoMetadata) {
										PARSELY.video.onPause(player, pausedVideoMetadata.id, {
											duration: pausedVideoMetadata.duration,
											image_url: pausedVideoMetadata.thumbnail,
											pub_date_tmsp: pausedVideoMetadata.publishDate,
											title: pausedVideoMetadata.title,
											author: pausedVideoMetadata.contentStudio,
											tags: pausedVideoMetadata.keywords,
											video_platform: platform
										});
									});
								}
							};

							window.PARSELY.video.addStrategy(strategy);
						};

						const watchedPlayerUIDs = [];

						function watchNewPlayers() {
							window.DTConnatixPlayers = window.DTConnatixPlayers || {};
							Object.keys(window.DTConnatixPlayers).forEach((playerUid) => {
								if (!watchedPlayerUIDs.includes(playerUid)) {
									watchedPlayerUIDs.push(playerUid);
									if (window.DTConnatixPlayers && window.DTConnatixPlayers[playerUid]) {
										watch(window.DTConnatixPlayers[playerUid]);
									}
								}
							});
						}

						DTEvent.on('dt-connatix-player-ready', watchNewPlayers);
						watchNewPlayers();
					}
				})();
/*  */
